        # Search: ^(\s*)(\*?)([_a-z0-9]*)\s?(\(.*$)
        # Replace: $1self.$3 = None  # $2$4
        # To get: self.id = None  # (Integer)
        self.id = None  # (Integer)
        self.catalogItem = None  # *(CatalogItemReference)
        self.chargeToId = None  # *(Integer)
        self.chargeToType = None  # *(Enum)
        self.description = None  # (String(2000))
        self.sequenceNumber = None  # (Number)
        self.quantity = None  # (Number)
        self.price = None  # (Number)
        self.cost = None  # (Number)
        self.discount = None  # (Number)
        self.priceMethod = None  # (Enum)
        self.billableOption = None  # *(Enum)
        self.agreement = None  # (AgreementReference)
        self.locationId = None  # (Integer)
        self.businessUnitId = None  # (Integer)
        self.vendor = None  # (CompanyReference)
        self.vendorSku = None  # (String(50))
        self.taxableFlag = None  # (Boolean)
        self.dropshipFlag = None  # (Boolean)
        self.specialOrderFlag = None  # (Boolean)
        self.phaseProductFlag = None  # (Boolean)
        self.cancelledFlag = None  # (Boolean)
        self.quantityCancelled = None  # (Number)
        self.cancelledReason = None  # (String(100))
