        self.cancelledBy = None  # (Integer)
        self.cancelledDate = None  # (String)
        self.warehouse = None  # (String)
        self.warehouseBin = None  # (String)
        self.purchaseDate = None  # (String)
        self.integrationXRef = None  # (String)
        self.listPrice = None  # (Number)
        self.serialNumberIds = None  # (Integer[])
        self.company = None  # (CompanyReference)
        self.forecastStatus = None  # (OpportunityStatusReference)
        self._info = None  # (Metadata)
        self.bypassForecastUpdate = None  # (Boolean)
        self.customFields = None  # (CustomFieldValue[])

